mapper.select:
select a.id id,
a.name name,
FORMAT(((a.story_score * 5 + a.role_score * 3 + a.img_quality_score * 3 + a.music_score * 1) / 12), 2) allScore,
a.story_score storyScore,
a.role_score roleScore,
a.img_quality_score imgQualityScore,
a.music_score musicScore,
b.type environmentType,
c.type type,
d.type rebornType,
e.type loveType,
f.type branchType,
g.type spType,
a.sex_level sexLevel,
a.language language,
a.area area,
if(a.status = 1, '已玩', '未玩') status
from (((((galgame_main a left join galgame_type b on a.environment_type = b.id)
left join galgame_type c on a.type = c.id)
left join galgame_type d on a.reborn_type = d.id)
left join galgame_type e on a.love_type = e.id)
left join galgame_type f on a.branch_type = f.id)
left join galgame_type g on a.sp_type = g.id
where a.deleted = 0
and b.deleted = 0
and c.deleted = 0
and d.deleted = 0
and e.deleted = 0
and f.deleted = 0
and g.deleted = 0

    and a.name like CONCAT('%',#{name} ,'%')


    and a.story_score = #{storyScore}


    and a.role_score = #{roleScore}


    and a.img_quality_score = #{imgQualityScore}


    and a.music_score = #{musicScore}


    and a.environment_type = #{environmentType}


    and a.type = #{type}


    and a.reborn_type = #{rebornType}


    and a.love_type = #{loveType}


    and a.branch_type = #{branchType}


    and a.sp_type = #{spType}


    and a.sex_level = #{sexLevel}


    and a.language = #{language}


    and a.area = #{area}


    and a.status = #{status}
