mapper.select:
SELECT a.id, a.mid mid, a.music_name musicName, a.anime_name animeName, b.type type, a.time time, a.singer singer, a.score score, a.form form, a.source source, c.downloadType downloadType, d.addType addType, a.year year, f.quarter_CN quarter, if(a.year is null and f.quarter_CN is null , '', concat(a.year, '年', f.quarter_CN)) bingeTime from (((music_main a left join music_type b on a.type = b.id) left join music_downloadtype c on a.download_type = c.id) left join music_addtype d on a.add_type = d.id) left join quarter f on a.quarter = f.id where a.deleted = 0 and b.deleted = 0 and c.deleted = 0 and d.deleted = 0
and a.music_name like CONCAT('%',#{musicName} ,'%')
and a.mid like CONCAT('%',#{mid} ,'%')
and a.anime_name like CONCAT('%',#{animeName} ,'%')
and a.type = #{type}
and a.time = #{time}
and a.singer like CONCAT('%',#{singer} ,'%')
and a.score = #{score}
and a.form = #{form}
and a.source = #{source}
and a.download_type = #{downloadType}
and a.add_type = #{addType}
and a.year like CONCAT('%',#{year} ,'%')
and a.quarter = #{quarter}

mapper.update:
update music_main a


        a.mid = #{mid} ,


where id = #{id} and deleted = 0;
